(190, 1, '2015-07-02 17:57:45', '2015-07-02 16:57:45', '<h1 class="sifirmargin">Price List</h1>\r\n<h2>Manual Lessons</h2>\r\n<div>\r\n<table class="table table-bordered" style="width:600px;">\r\n<thead>\r\n<tr>\r\n<th>Hours Of Lessons</th>\r\n<th>Price</th>\r\n</tr>\r\n</thead>\r\n<tbody>\r\n<tr>\r\n<td>Single Lesson</td>\r\n<td>£20</td>\r\n</tr>\r\n<tr>\r\n<td>5</td>\r\n<td>£98</td>\r\n</tr>\r\n<tr>\r\n<td>10</td>\r\n<td>£190</td>\r\n</tr>\r\n<tr>\r\n<td>20</td>\r\n<td>£370</td>\r\n</tr>\r\n<tr>\r\n<td>30</td>\r\n<td>£540</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</div>\r\n<h2>Automatic Driving Lessons</h2>\r\n<div>\r\n<table class="table">\r\n<thead>\r\n<tr>\r\n<th>Hours Of Lessons</th>\r\n<th>Price</th>\r\n</tr>\r\n</thead>\r\n<tbody>\r\n<tr>\r\n<td>1 hour</td>\r\n<td>£23</td>\r\n</tr>\r\n<tr>\r\n<td>5 hours</td>\r\n<td>£110</td>\r\n</tr>\r\n<tr>\r\n<td>10 hours</td>\r\n<td>£215</td>\r\n</tr>\r\n<tr>\r\n<td>20 hours</td>\r\n<td>£420</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</div>\r\n<h2>Hire a car for driving test</h2>\r\n<div>\r\n<table class="table">\r\n<thead>\r\n<tr>\r\n<th>Test Location</th>\r\n<th>Price</th>\r\n</tr>\r\n</thead>\r\n<tbody>\r\n<tr>\r\n<td>Wood Green - Chingford - Wanstead</td>\r\n<td>£80</td>\r\n</tr>\r\n<tr>\r\n<td>Hendon - Mill Hill - Barnet - Enfield - Barking</td>\r\n<td>£90</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</div>\r\n<h4>Pass Plus Lessons</h4>\r\n<div>\r\n<table class="table">\r\n<thead>\r\n<tr>\r\n<th></th>\r\n<th></th>\r\n</tr>\r\n</thead>\r\n<tbody>\r\n<tr>\r\n<td>Motorway Lessons, 3 hours</td>\r\n<td>£75</td>\r\n</tr>\r\n<tr>\r\n<td>Pass - Plus Course</td>\r\n<td>£150</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</div>\r\n<h3>Each lesson is for a full one hour. Collection from and return to your residence in most areas.</h3>\r\n<strong>DSA''s recommends:</strong>Those who pass their driving test have had, on average, about<span style="color: #339966;">45 hours</span>of professional training combined with<span style="color: #339966;">22 hours</span>of private practice. Learners who prepare this way, with a combination of plenty of professional training and plenty of practice, do better in the test.<a href="http://www.direct.gov.uk/en/Motoring/LearnerAndNewDrivers/PracticalTest/DG_4022483">read more.</a>\r\n\r\nUntitled Document\r\n\r\n&nbsp;', 'Price List', '', 'inherit', 'open', 'open', '', '26-revision-v1', '', '', '2015-07-02 17:57:45', '2015-07-02 16:57:45', '', 26, 'http://www.declondon.com/2015/07/02/26-revision-v1/', 0, 'revision', '', 0),
